mapper.update:
UPDATE user
SET
	gmt_modified=now()
	 ,login_name=#{loginName}
	 ,nick_name=#{nickName}
	 ,photo=#{photo}
	 ,come_from=#{comeFrom}
	 ,come_sn=#{comeSN}
	 ,type=#{type}
	 ,status=#{status}
	 ,device_token=#{deviceToken}
	 ,device_type=#{deviceType}
	 ,voip_client_no=#{voipClientNo}
     ,voip_client_pwd=#{voipClientPwd}
WHERE id=#{id}

mapper.select:
     SELECT

     FROM user

     	 AND id=#{id}
     	 AND login_name=#{loginName}
     	 AND status=#{status}
     	 AND mobile=#{mobile}
     	 AND come_from=#{comeFrom}
     	 AND come_sn=#{comeSN}
     	 AND invitor_id=#{invitorId}
     	 AND type=#{type}
     	 AND device_type=#{deviceType}
     	 AND device_token=#{deviceToken}
          AND voip_client_no=#{voipClientNo}
          AND voip_client_pwd=#{voipClientPwd}
     	 AND id IN 

						#{userId}



     limit #{offset},#{limit}

select

from user
where id in (
	select friend_id
	from my_friend
	where my_id = #{userId}
	and is_deleted = 0
)
and mobile = #{friendMobile}
and is_deleted = 0

mapper.insert:
insert into my_friend (
	gmt_created, gmt_modified, gmt_invited, gmt_confirmed, my_id, friend_id, relation
) values (
	now(), now(), now(), now(), #{myId}, #{friendId}, #{relation}
)
